sections.css:
// Common styles across buttons
%button-base {
  border: 2px solid $color-text-invert;
  border-radius: rem-calc(25);
  display: inline-block;
  font-family: $font-family-display;
  @include font-size-regular;
  font-weight: $font-weight-extrablack;
  line-height: 1;
  min-width: rem-calc(125);
  padding: rem-calc(14 30);
  text-align: center;
  text-decoration: none;
  transition: $button-transition;

  @include media($bp-small) {
    min-width: rem-calc(150);
  }
}

.button--primary {
  @extend %button-base;
  background: $color-text-invert;
  color: $color-text;
  letter-spacing: 1px;
  text-transform: uppercase;

  &:hover,
  &:focus {
    background: transparent;
    color: $color-text-invert;
  }
}

.button--outline {
  @extend %button-base;
  background: transparent;
  letter-spacing: 1px;

  &--gray {
    border-color: $color-text;
    color: $color-text;
    opacity: .7;

    &:hover,
    &:focus {
      color: $color-text;
      opacity: 1;
    }
  }

  &--white {
    border-color: $color-text-invert;
    color: $color-text-invert;

    &:hover,
    &:focus {
      background: $color-text-invert;
      color: $color-primary;
    }
  }

}

.button--pill {
  @extend %button-base;
  background: $color-primary;
  border: 0;
  border-radius: rem-calc(15);
  color: $color-text-invert;
  @include font-size($font-size-xx-small);
  margin: rem-calc(0 2 4 0);
  min-width: 0;
  padding: rem-calc(10 17);
  @include text-uppercase;

  &:hover,
  &:focus {
    background: $color-secondary;
    color: $color-text-invert;
  }

  @include media($bp-small) {
    min-width: 0;
  }
}

.button--pill--alt {
  background: $color-secondary;

  &:hover,
  &:focus {
    background: $very-dark-blue;
  }
}

.button--simple {
  @extend %button-base;
  background: $color-primary;
  border: none;
  color: $color-text-invert;

  &:hover,
  &:focus {
    background: $color-secondary;
    color: $color-text-invert;
    outline: none;
  }
}

.button--simple--alt {
  background: $color-secondary;

  &:hover,
  &:focus {
    background: $very-dark-blue;
  }
}

// Size Variations

// Full-width
.button--full-width {
  width: 100%;
}

.button--text {
  color: $color-primary;
  min-height: 0;

  &:hover,
  &:focus {
    color: $color-secondary;
    outline: none;
  }
}

.sticker {
  background: $color-primary;
  border-radius: rem-calc($sticker-size-mobile);
  color: $white;
  display: block;
  font-family: $font-family-display;
  @include font-size(13, 11.375);
  height: rem-calc($sticker-size-mobile);
  text-align: center;
  text-decoration: none;
  @include text-uppercase;
  white-space: nowrap;
  width: rem-calc($sticker-size-mobile);
  //override text-uppercase
  line-height: rem-calc($sticker-size-mobile);

  @include media($bp-large) {
    border-radius: rem-calc($sticker-size);
    height: rem-calc($sticker-size);
    line-height: rem-calc($sticker-size);
    width: rem-calc($sticker-size);
  }
}

a.sticker {
  &:hover,
  &:focus {
    background: $color-secondary;
    color: $white;
  }
}

.flexbox .sticker {
  align-items: center;
  display: flex;
  flex-direction: column;
  justify-content: center;
  line-height: 1.2;
}

// Local Variables
$height-banner-logo-small: $height-banner-logo * 1.3333333333;
$height-banner-logo-large: $height-banner-logo * 2.4;
$width-banner-logo-small: $width-banner-logo * 1.33333333333;
$width-banner-logo-large: $width-banner-logo * 2.4;

.c-banner {
  background: $color-background;
  box-shadow: 0 2px 4px 0 $color-shadow;
  height: rem-calc($height-banner);
  position: relative;
  z-index: $z-index-header;

  @include media($bp-medium) {
    height: rem-calc($height-banner-large);
  }
}

.c-banner__logo-wrapper {
  background: $color-background;
  box-shadow: 0 0 4px 0 $color-shadow;
  display: block;
  height: rem-calc($height-banner-logo);
  @include layout-inner-left-edge;
  padding: 0 5px 8px;
  position: absolute;
  top: 0;
  width: rem-calc($width-banner-logo);

  @include media($bp-small) {
    height: rem-calc($height-banner-logo-small);
    width: rem-calc($width-banner-logo-small);
  }

  @include media($bp-medium) {
    height: rem-calc($height-banner-logo-large);
    padding: 0 16px 20px 14px;
    width: rem-calc($width-banner-logo-large);
  }
}

// Banner Logo
.c-banner__logo {
  @include image-replace('/wp-content/themes/guny/assets/img/NYCDOH_GrowingUP_logo_CMYK_Mobile_Final.png');
  background-position: center bottom;
  height: 100%;
  width: 100%;
}

.c-banner__nav {
  float: right;
  line-height: 1;
  padding-top: rem-calc(18);

  @include media($bp-medium) {
    padding-top: rem-calc(20);
  }
}

.c-banner__link {
  color: $color-primary;
  font-family: $font-family-display;
  font-size: rem-calc(12);
  font-weight: $font-weight-extrablack;
  position: relative;
  text-decoration: none;
  @include text-uppercase;

  &:hover,
  &:focus {
    color: $color-secondary;
  }

  @include media($bp-medium) {
    font-size: rem-calc($font-size-small);
    letter-spacing: 1.1px;
  }
}

// Banner Icon
//
// SVG icon. Does not appear in styleguide
.c-banner__icon {
  display: inline-block;
  height: 1.25em;
  margin-top: rem-calc(-4);
  transition: all .25s ease-in;
  vertical-align: middle;
  width: 1.25em;
}

.c-banner__icon__search {
  opacity: 1;

  .is-active & {
    opacity: 0;
  }
}

.c-banner__icon__close {
  left: 0;
  opacity: 0;
  position: absolute;
  top: rem-calc(2);

  .is-active & {
    opacity: 1;
  }
}

.c-footer {
  background: $color-background-footer;
  color: $color-text-invert;
  padding: 60px 0;
}

// Footer Section
.c-footer__section {
  @include media($bp-large) {
    @include span-columns(6);
  }
}

.c-footer__logos {
  @include row;
}

// Individual Footer Logo
.c-footer__logo {
  @include span-columns(6);

  + .c-footer__logo {
    @include omega;
  }
  @include media($bp-medium) {
    @include span-columns(4 of 12);

    + .c-footer__logo {
      @include shift(2);
    }
  }
}

// Footer Logo Image
.c-footer__logo img {
  max-height: rem-calc(77);
  max-width: 100%;

  @include media($bp-medium) {
    max-height: none;
  }
}

.c-footer__nav {
  @include row;
  line-height: 1.2;
  padding-top: 35px;

  @include media($bp-large) {
    padding-top: 40px;
  }
}

// Footer Navigation Column
.c-footer__column {
  @include span-columns(6 of 12);
  @include omega(even);
}

.c-footer__nav-item {
  list-style: none;
  margin-bottom: 8px;
}

.c-footer__nav-item--spaced {
  margin-top: 54px;
}

// Footer Link
.c-footer__link {
  color: $color-text-invert;
  font-family: $font-family-display;
  font-weight: $font-weight-extrablack;
  text-decoration: none;

  &:hover,
  &:focus {
    color: $color-secondary;
  }
}

// Footer Signup Area
.c-footer__signup {
  margin-top: 80px;

  @include media($bp-large) {
    margin-top: 0;
    @include span-columns(4);
    @include shift(2);
  }
}

.c-footer__footer {
  font-family: $font-family-display;
  font-size: rem-calc(14);
  font-weight: $font-weight-extrablack;
  line-height: 1.2;
  margin-top: 65px;
}

.c-list-box {
  display: block;
  padding: rem-calc(6 4 2);
}

.c-list-box--tertiary {
  padding: 0;
}

// List Box Item
//
// Section within the list box. Encompasses the list heading and any sublists.
.c-list-box__item {
  display: block;
  color: $color-text-invert;
  list-style: none;
  margin-bottom: rem-calc(4);
  position: relative;
}

.c-list-box--secondary .c-list-box__item {
  margin-bottom: rem-calc(2);
}

// List Box Heading
//
// Top level list item or section heading.
// If used with the accordion pattern, this will be whatever the accordion drawer
// heading is.
//
.c-list-box__heading {
  border-radius: rem-calc(3);
  display: block;
  font-family: $font-family-display;
  @include font-size-x-large;
  font-weight: $font-weight-extrablack;
  margin-bottom: 0;
  text-align: left;
  width: 100%;
}

.c-list-box--primary .c-list-box__heading,
.c-list-box--quaternary .c-list-box__heading {
  background: $color-primary;
  color: $color-text-invert;
}

.c-list-box--primary .c-list-box__heading {
  @include text-uppercase;
  padding: rem-calc(12 20);

  @include media($bp-large) {
    padding: rem-calc(28 40);
  }
}

.c-list-box--secondary .c-list-box__heading,
.c-list-box--quaternary .c-list-box__heading {
  padding: rem-calc(13 28);
}

.c-list-box--secondary .c-list-box__heading {
  background: $color-background-shade;
  border-radius: rem-calc(3);
  color: $color-primary;

  .icon {
    color: $color-text-weak;
  }

  // :focus styling is necessary for keyboard users so that it's clear what item is currently selected
  &:hover,
  &:focus {
    color: $color-secondary;
    outline: none;

    .icon {
      color: $color-text;
    }
  }
}

.c-list-box--tertiary .c-list-box__heading {
  background: $color-primary;
  color: $color-text-invert;
  @include font-size-large;
  line-height: 1.1;
  min-height: rem-calc(56);
  padding: rem-calc(10 30);
}

.c-list-box--quaternary .c-list-box__heading {
  @include media($bp-large) {
    padding: rem-calc(28);
  }
}

// List Box Subheading
//
// Optional subtitle on a list box heading
.c-list-box__subheading {
  display: block;
  @include font-size-large;
  font-weight: $font-weight-medium;
}

.c-list-box--primary .c-list-box__subheading {
  text-transform: none;
}

// List Box Content
//
// The sublist of items within a box
.c-list-box--primary .c-list-box__content {
  background: $color-primary;
  color: $color-text-invert;
}

.c-list-box--secondary .c-list-box__content {
  background: $color-primary;
}

.c-list-box--secondary .c-list-box__content,
.c-list-box--quaternary .c-list-box__content {
  font-family: $font-family-display;
  font-weight: $font-weight-extrablack;
}

.c-list-box--quaternary .c-list-box__content {
  background: $color-secondary;
  border-radius: 0 0 3px 3px;
  color: $color-text-invert;
  margin-top: -1px;
}

// List Box Subitem
//
// Item within a sublist. If the sublist is a list of links, this class may be applied
// to the a element rather than the li element.
.c-list-box__subitem {
  display: block;
  text-decoration: none;
}

.c-list-box--primary .c-list-box__subitem {
  padding: rem-calc(3 20);

  &:first-child {
    padding-top: rem-calc(30);
  }

  &:last-child {
    padding-bottom: rem-calc(30);
  }

  @include media($bp-large) {
    padding: rem-calc(3 40);

    &:last-child {
      padding-bottom: rem-calc(40);
    }

  }
}

.c-list-box--secondary .c-list-box__subitem {
  @include text-uppercase;

  &:hover,
  &:focus {
    background: $color-secondary;
  }
}

.c-list-box--secondary .c-list-box__subitem,
.c-list-box--quaternary .c-list-box__subitem {
  color: $color-text-invert;
  @include font-size-small;
  padding: rem-calc(16 28);
}

.c-list-box--tertiary .c-list-box__subitem {
  background: $color-background-shade;
  color: $color-text;
  line-height: 1.2;
  padding: rem-calc(15 30 15 42);
  text-indent: rem-calc(-27);
  margin-bottom: rem-calc(2);

  .icon {
    color: rgba($color-text, .2);
    margin-right: rem-calc(12);
    margin-top: rem-calc(2);
  }

  @include media($bp-large) {
    padding-left: rem-calc(60);
  }
}

.c-list-box--quaternary .c-list-box__subitem {
  &:hover,
  &:focus {
    background: $very-dark-blue;
  }
}

// List Box Border
.c-list-box__border {
  border-radius: 3px;
  padding: rem-calc(5 0);
}

.c-block-list--shade {
  background: $color-background-shade;
}

.c-block-list__heading {
  color: $very-dark-gray-transparent;
  @include font-size-small;
  margin-bottom: rem-calc(-20);
  @include text-uppercase;
}

// Block List Text
//
// Content that appears before the block list
.c-block-list--shade .c-block-list__text {
  color: $dark-gray;
}

// Block List Link
.c-block-list__link {
  float: right;
  text-decoration: none;
  text-transform: none;

  &:hover,
  &:focus {
    color: $color-text;
  }
}

// Block List Inner
//
// Inner container within a block list
.c-block-list__inner {
  margin-bottom: rem-calc(35);
  margin-top: rem-calc(35);

  @include media($bp-medium) {
    margin-bottom: rem-calc(45);
    margin-top: rem-calc(30);
  }
}

// Block List Item
//
// Individual item within the list
.c-block-list__item {
  padding: rem-calc(8 0);
}

.c-details-list {
  background: $color-background-shade;
  border-radius: 3px;
  padding: rem-calc(10 0);
}

// Details List Term
.c-details-list__term {
  font-family: $font-family-display;
  @include font-size-large;
  font-weight: $font-weight-extrablack;
  line-height: 1.3;
  padding: rem-calc(15 20 0);

  @include media($bp-medium) {
    padding: rem-calc(17 30 0);
  }
}

// Details List Description
.c-details-list__descr {
  padding: rem-calc(0 20 15);

  @include media($bp-medium) {
    padding: rem-calc(0 30 17);
  }
}

.c-details-list__descr + .c-details-list__term {
  border-top: 2px solid $white;
}

.form-field,
.facetwp-search {
  appearance: none;
  background: $white;
  border: 1px solid transparent;
  border-radius: rem-calc(3);
  color: $color-text;
  @include font-size-small;
  font-weight: $font-weight-semibold;
  padding: rem-calc(10 15);
}

input[type="search"].form-field {
  appearance: none;
}

// Size Variations

// Large
.form-field--large {
  @include font-size-x-large;
  line-height: normal;
  padding: rem-calc(10 40 10 20);

  @include media($bp-medium) {
    padding: rem-calc(20 60 20 25);
  }
}

.form-field--full-width {
  width: 100%;
}

// Icons

// Layout
.form-field__icon-container {
  position: relative;
}

.form-field__icon {
  display: block;
  color: $color-primary;
  cursor: pointer;
  height: rem-calc(20);
  margin-top: rem-calc(-10);
  position: absolute;
  right: rem-calc(20);
  top: 50%;
  width: rem-calc(20);

  @include media($bp-medium) {
    right: rem-calc(25);
  }

  &:hover,
  &:focus {
    color: $color-secondary;
  }

  + .form-field__icon {
    right: rem-calc(50);

    @include media($bp-medium) {
      right: rem-calc(55);
    }
  }
}

.form-field__icon--weak {
  color: $color-text-weak;

  &:hover,
  &:focus {
    color: $color-text;
  }
}

// Icon Size Variations

// Large
.form-field__icon--large {
  height: rem-calc(20);
  margin-top: rem-calc(-15);
  width: rem-calc(20);

  @include media($bp-medium) {
    height: rem-calc(30);
    margin-top: rem-calc(-15);
    width: rem-calc(30);

    + .form-field__icon--large {
      right: rem-calc(65);
    }
  }
}

.checkbox {
  cursor: pointer;
  position: relative;
}

.checkbox__field {
  position: absolute;
  opacity: 0;
  z-index: -1;
}

.checkbox__indicator {
  background: $white;
  border-radius: 3px;
  border: 2px solid $white;
  display: inline-block;
  color: transparent;
  height: rem-calc(20);
  width: rem-calc(20);
  vertical-align: middle;
}

.checkbox__field:checked + .checkbox__indicator {
  color: $color-secondary;
}

.checkbox__field:focus + .checkbox__indicator {
  border-color: $dark-gray;
}

.checkbox__field.is-error + .checkbox__indicator {
  border-color: $color-secondary;
}

.signup-form {
  @include font-size-small;
  h2 {
    margin-bottom: 0;
  }
}

// Signup Form Error
.signup-form__error {
  p {
    margin-top: rem-calc(20);
  }
}

.signup-form__input {
  display: inline-block;
  margin: rem-calc(24) 0 0;
  position: relative;
  z-index: 1;
}

.signup-form__input--long {
  width: 100%;

  @include media($bp-medium) {
    width: rem-calc(240);
  }
}

.signup-form__input--short {
  width: 100%;
  @include media($bp-medium) {
    max-width: rem-calc(153);
    min-width: rem-calc(100);
    width: calc(100% - 15.3125rem);
  }
}

.signup-form__input--full {
  width: 100%;
}

// Signup Form Field
//
// .is-error - Error state
//
// Text input field
.signup-form__field {
  border: 2px solid transparent;
  width: 100%;

  &:focus {
    border-color: $dark-gray;
    outline: none;
  }

  &.is-error {
    border-color: $color-secondary;
  }
}

// Signup Form Label
.signup-form__label {
  overflow: hidden;
  padding: 0 rem-calc(15);
  position: absolute;
  width: 100%;
}

.no-csstransforms3d .signup-form__label {
  bottom: rem-calc(10);
}

.no-csstransforms3d .is-filled .signup-form__label {
  bottom: 100%;
}

.csstransforms3d .signup-form__label,
.styleguide .signup-form__label {
  bottom: 100%;
  transform: translate3d(0, #{rem-calc(37)}, 0);
  transition: transform 250ms ease-in-out;
}

.csstransforms3d .is-filled .signup-form__label,
.styleguide .is-filled .signup-form__label {
  transform: translate3d(0, 0, 0);
}

// Signup Form Label Content
//
// Inner content within a label. The data-content
.signup-form__label-content {
  color: $color-secondary;
  display: block;
  position: relative;
  width: 100%;
}

.csstransforms3d .signup-form__label-content {
  transition: transform 250ms ease-in-out;

  &:after {
    bottom: 100%;
    color: $color-text-invert;
    content: attr(data-content);
    font-family: $font-family-display;
    @include font-size-x-small;
    font-weight: $font-weight-extrablack;
    left: 0;
    position: absolute;
    text-transform: uppercase;
    width: 100%;
  }
}

.no-csstransforms3d .is-filled .signup-form__label-content {
  color: $color-text-invert;
  font-family: $font-family-display;
  @include font-size-x-small;
  font-weight: $font-weight-extrablack;
  text-transform: uppercase;
}

.csstransforms3d .is-filled .signup-form__label-content,
.styleguide .is-filled .signup-form__label-content {
  transform: translate3d(0, 100%, 0);
}

// Signup Form Fieldset
.signup-form__fieldset {
  margin-top: rem-calc(24);
}

// Signup Form Checkbox
//
// Wrapper around checkbox input, label, and hidden field (if its a Tools checkbox)
.signup-form__checkbox {
  display: inline-block;
  line-height: rem-calc(20);
  margin-right: rem-calc(20);
  margin-top: rem-calc(10);
  vertical-align: middle;
}

// Signup Form Button
.signup-form__button {
  margin-top: rem-calc(20);
}

.c-signup-form__success {
  font-family: $font-family-display;
  @include font-size(21, 24);
  font-weight: $font-weight-extrablack;
  text-align: center;
}

.c-content-tout {
  color: $color-text-weak;
  @include font-size-small;
}

.c-content-tout__title {
  color: $color-text;
  font-family: $font-family-display;
  @include font-size-large;
  font-weight: $font-weight-extrablack;
  letter-spacing: rem-calc(.44);
  line-height: 1.2;
  margin: 0;
  text-decoration: none;

  a {
    text-decoration: inherit;
  }
}

.c-content-tout__subtitle {
  font-family: $font-family-display;
  font-weight: $font-weight-extrablack;
  margin: rem-calc(7 0 3);
  text-transform: uppercase;

  @include media($bp-small) {
    margin: rem-calc(10 0 1);
  }

  .icon--xsmall {
    position: relative;
    top: 2px;
    vertical-align: top;

    @include media($bp-small) {
      top: 3px;
    }
  }
}

.c-content-tout__link {
  @include font-size(14);
  text-decoration: underline;

  a {
    &:hover,
    &:focus {
      color: $color-text;
    }
  }
}

.c-card {
  background: $color-background;
  display: flex;
  flex-flow: column;
  margin-right: rem-calc($sticker-size-mobile / 2);
  padding: rem-calc(25 ($sticker-size-mobile / 2) + 10 25 21);
  position: relative;

  @include media($bp-large) {
    margin-right: rem-calc($sticker-size / 2);
    padding: rem-calc(30);
    padding-right: rem-calc(($sticker-size / 2) + 10);
  }
}

.c-card--hero {
  background-position: top center;
  background-size: 100% auto;
  background-repeat: no-repeat;
  padding-top: percentage(123/335);

  @include media($bp-medium) {
    padding-top: percentage(266/760);
  }
}

.c-card__sticker {
  order: 1;
  position: absolute;
  right: rem-calc(-$sticker-size-mobile / 2);
  top: rem-calc(10);

  .event-day {
    display: none;
  }

  @include media($bp-large) {
    right: rem-calc(-$sticker-size / 2);
  }
}

.flexbox .c-card__sticker {
  .event-day,
  .event-month-date {
    display: block;
  }
}

.c-card__title {
  font-family: $font-family-display;
  @include font-size-xx-large;
  font-weight: $font-weight-extrablack;
  line-height: 1.2;
  margin: rem-calc(0 0 12);
  order: 3;
  text-decoration: none;

  a {
    text-decoration: inherit;
  }
}

.c-card__tags {
  margin: rem-calc(0 0 10);
  order: 5;
}

.c-card__subtitle {
  color: $color-text-weak;
  font-family: $font-family-display;
  font-weight: $font-weight-extrablack;
  @include font-size(15);
  letter-spacing: .5px;
  margin: rem-calc(0 0 10);
  order: 4;

  .icon--xsmall {
    position: relative;
    top: rem-calc(3);
    vertical-align: top;
  }

  p + p {
    margin-top: 0;
  }
}

.c-card__subtitle strong {
  color: $color-text;
  font-weight: inherit;
}

.c-card--subtitle-first .c-card__subtitle {
  order: 2;
}

.c-card__body {
  @include font-size-small;
  margin: rem-calc(0 0 5);
  order: 6;
}

.c-card__link {
  color: $color-text-weak;
  @include font-size(14);
  order: 7;

  a {
    &:hover,
    &:focus {
      color: $color-text;
    }
  }
}

.c-card__label {
  background: $color-background;
  border-radius: 3px;
  box-shadow: 0 2px 4px 0 $color-shadow;
  color: $dark-gray;
  font-family: $font-family-display;
  @include font-size-x-small;
  font-weight: $font-weight-extrablack;
  padding: rem-calc(10);
  position: absolute;
  @include text-uppercase;
  top: rem-calc(-16);
}

.c-hero {
  background-position: top center;
  background-repeat: no-repeat;
  background-size: contain;
  color: $color-text-invert;
  overflow: hidden;
  padding: percentage(94/375) 0 rem-calc(33);
  position: relative;
  text-align: center;

  @include media($bp-medium) {
    background-size: cover;
    text-align: left;
    padding: rem-calc(92 40 80);
  }
}

.c-hero--light {
  color: $color-text;
  padding: percentage(57/375) 0 rem-calc(65);

  @include media($bp-medium) {
    padding: rem-calc(92 40 36);
  }
}

.c-hero {
  @include media($bp-xlarge) {
    padding: rem-calc(113 80 60);
  }
}

.c-hero--large {
  padding: percentage(126/375) 0 rem-calc(65);

  @include media($bp-medium) {
    padding: rem-calc(165 40 80);
  }

  @include media($bp-xlarge) {
    padding: rem-calc(227 80 80);
  }
}

.c-hero--long-text {
  text-align: left;
}

// Hero Background Variations
.c-hero--bg-primary {
  background: $color-primary;
}

.c-hero--bg-secondary {
  background-color: $color-secondary;
}

// Hero Title
//
// .c-hero__title--long - Variation for longer titles. Matches h1 styling
//
.c-hero__title {
  @include font-size-xxx-large;
  line-height: 1;
  padding-top: rem-calc(35);
  text-transform: uppercase;
  // Set z-index for text to appear on top of bg image
  z-index: 1;
}

.c-hero__title,
.c-hero__title--long {
  position: relative;
}

.flexbox .c-hero__title,
.flexbox .c-hero__title--long {
  @include media($bp-medium) {
    display: flex;
    flex-flow: row nowrap;
    align-items: center;
  }
}

// Hero Sticker
.c-hero__sticker {
  background-color: $color-secondary;
  bottom: 100%;
  box-shadow: 0 2px 4px 0 $color-shadow;
  display: inline-block;
  left: 0;
  //top: rem-calc($sticker-size-mobile / -2);
  position: absolute;

  @include media($bp-medium) {
    box-shadow: none;
    height: rem-calc(65);
    line-height: rem-calc(65);
    margin-right: rem-calc(20);
    position: static;
    width: rem-calc(65);
  }
}

// Hero Tagline
.c-hero__tagline {
  font-family: $font-family-display;
  font-size: rem-calc($font-size-xx-large * $font-size-ratio);
  font-weight: $font-weight-extrablack;
  line-height: 1.2;
  margin-top: rem-calc(10);
  position: relative;
  z-index: 1; // Set z-index for text to appear on top of bg image

  @include media($bp-medium) {
    font-size: rem-calc($font-size-xx-large);
  }

  @include media($bp-large) {
    @include span-columns(6);
    @include omega;
  }
}

.c-hero--long-text .c-hero__tagline {
  font-size: rem-calc($font-size-large * $font-size-ratio);
  line-height: 1.3;
  margin-top: 0;

  + .c-hero__tagline {
    margin-top: rem-calc(15);
  }

  @include media($bp-medium) {
    font-size: rem-calc($font-size-large);
  }

  @include media($bp-large) {
    @include span-columns(9);
    @include omega;
  }
}

// Hero Frill
//
// Decorative element (i.e. a paint splatter) added to the hero
.c-hero__frill {
  height: auto;
  position: absolute;
  top: percentage(60/375);
  right: percentage(-50/375);
  width: percentage(140/375);

  @include media($bp-medium) {
    bottom: percentage(-87/480);
    top: auto;
    right: percentage(-114/1440);
    width: percentage(404/1440);
  }

  @include media($bp-xlarge) {
    bottom: -87px;
    right: -114px;
    width: 404px;
  }
}

// Hero Full BG
//
// Full background overlay added to the hero
.c-hero__full-bg {
  height: 100%;
  left: 0;
  min-width: 100%;
  position: absolute;
  top: 0;
  width: auto;
}

// Hero Label
.c-hero__label {
  display: block;
  font-family: $font-family-display;
  font-weight: $font-weight-extrablack;
  @include font-size(21,24);
  margin-bottom: rem-calc(15);
  text-align: center;
}

.styleguide-example {
  .c-side-hero {
    height: 100%;
  }
}

.c-side-hero__image {
  background: $color-primary center center no-repeat;
  background-size: cover;
  height: 53.3333333vw;

  @include media($bp-large) {
    height: 100%;
  }

  .styleguide-example & {
    background-image: url(/wp-content/uploads/2016/07/hero-home.jpg);
  }
}

.c-side-hero__content {
  color: $white;
  padding: rem-calc(40 15);

  @include media($bp-mobile-only) {
    background: $color-primary;
    @include splatter(top, left);
    @include splatter(bottom, right);
  }

  @include media($bp-large) {
    bottom: 0;
    left: 0;
    padding: rem-calc(60);
    position: absolute;
    width: 60%;
  }
}

@include media($bp-large) {
  .js {
    .c-side-hero__content {
      position: fixed;
    }

    .is-bottom .c-side-hero__content {
      left: rem-calc(-4); // accounts for border on container
      position: absolute;
      width: 100%;
    }
  }
}

.c-side-hero__title {
  @include font-size-x-large;
  line-height: 1.2;
  position: relative;
  text-align: center;
  z-index: $z-index-hero-title;

  @include media($bp-large) {
    margin-bottom: rem-calc(20);
    text-align: left;
  }
}

.c-side-hero__title__large {
  @include font-size-xxx-large;
  text-transform: uppercase;
  white-space: nowrap;

  .rotating-text__entry {
    display: block;
    width: 100%;
  }
}

.c-arrow-pager {
  font-family: $font-family-display;
  font-weight: $font-weight-extrablack;
  @include font-size-x-large;
  padding-top: 10px;
  margin: 0 0 35px;
  text-align: center;
  @include text-uppercase;

  @include media($bp-large) {
    padding-top: 0;
  }
}

.c-arrow-pager__arrow {
  color: $very-dark-gray-transparent;
  text-decoration: none;
}

.c-tip {
  @include row;
  background: $color-primary;
  color: $color-text-invert;
  padding-top: rem-calc($sticker-size-mobile / 2);
  position: relative;
  margin-top: rem-calc(($sticker-size-mobile / 2) + $horizontal-gutter);

  @include media($bp-large) {
    padding-top: rem-calc($sticker-size / 2);
    margin-top: rem-calc(($sticker-size / 2) + $horizontal-gutter);
  }
}

// Tip Sticker
.c-tip__sticker {
  margin-left: rem-calc($sticker-size-mobile / -2);
  position: absolute;
  left: 50%;
  top: rem-calc($sticker-size-mobile / -2);

  @include media($bp-large) {
    margin-left: rem-calc($sticker-size / -2);
    top: rem-calc($sticker-size / -2);
  }
}

// Tip Inner Wrapper
// Optiona. Used if you want to restrict the width of tip content
.c-tip__inner {
  @include media($bp-medium) {
    @include span-columns(4 of 8);
    @include shift(2 of 8);
  }
}

// Tip Content
.c-tip__content {
  font-family: $font-family-display;
  @include font-size-x-large;
  font-weight: 900;
  line-height: 1.2;
}

// Tip Footer
.c-tip__footer {
  @include font-size-small;
  margin-top: 1em;

  p + p {
    margin-top: 0;
  }
}

// Tip Link
.c-tip__link {
  &:hover,
  &:focus {
    color: darken($color-text-invert, 15%);
  }
}

.c-alert {
  display: none;
}

.js .c-alert.is-open {
  background: $white;
  bottom: 0;
  display: block;
  left: 0;
  padding: 0 4px;
  position: fixed;
  text-align: center;
  width: 100%;
  z-index: $z-index-alert;

  p + p {
    margin-top: 0;
  }
}

// Inner banner content
.c-alert__banner {
  background: $very-dark-gray;
  border-radius: 3px 3px 0 0;
  color: $color-text-invert;
  font-family: $font-family-display;
  @include font-size-large;
  font-weight: $font-weight-extrablack;
  padding: 25px 0;
}

// Alert Banner Button
.c-alert__button {
  color: $color-text-invert;
  position: absolute;
  right: 10px;
  top: 0;

  &:hover,
  &:focus {
    color: darken($color-text-invert, 15%);
  }
}

section.css:
// Common styles across buttons
%button-base {
  border: 2px solid $color-text-invert;
  border-radius: rem-calc(25);
  display: inline-block;
  font-family: $font-family-display;
  @include font-size-regular;
  font-weight: $font-weight-extrablack;
  line-height: 1;
  min-width: rem-calc(125);
  padding: rem-calc(14 30);
  text-align: center;
  text-decoration: none;
  transition: $button-transition;

  @include media($bp-small) {
    min-width: rem-calc(150);
  }
}

.button--primary {
  @extend %button-base;
  background: $color-text-invert;
  color: $color-text;
  letter-spacing: 1px;
  text-transform: uppercase;

  &:hover,
  &:focus {
    background: transparent;
    color: $color-text-invert;
  }
}

.button--outline {
  @extend %button-base;
  background: transparent;
  letter-spacing: 1px;

  &--gray {
    border-color: $color-text;
    color: $color-text;
    opacity: .7;

    &:hover,
    &:focus {
      color: $color-text;
      opacity: 1;
    }
  }

  &--white {
    border-color: $color-text-invert;
    color: $color-text-invert;

    &:hover,
    &:focus {
      background: $color-text-invert;
      color: $color-primary;
    }
  }

}

.button--pill {
  @extend %button-base;
  background: $color-primary;
  border: 0;
  border-radius: rem-calc(15);
  color: $color-text-invert;
  @include font-size($font-size-xx-small);
  margin: rem-calc(0 2 4 0);
  min-width: 0;
  padding: rem-calc(10 17);
  @include text-uppercase;

  &:hover,
  &:focus {
    background: $color-secondary;
    color: $color-text-invert;
  }

  @include media($bp-small) {
    min-width: 0;
  }
}

.button--pill--alt {
  background: $color-secondary;

  &:hover,
  &:focus {
    background: $very-dark-blue;
  }
}

.button--simple {
  @extend %button-base;
  background: $color-primary;
  border: none;
  color: $color-text-invert;

  &:hover,
  &:focus {
    background: $color-secondary;
    color: $color-text-invert;
    outline: none;
  }
}

.button--simple--alt {
  background: $color-secondary;

  &:hover,
  &:focus {
    background: $very-dark-blue;
  }
}

// Size Variations

// Full-width
.button--full-width {
  width: 100%;
}

.button--text {
  color: $color-primary;
  min-height: 0;

  &:hover,
  &:focus {
    color: $color-secondary;
    outline: none;
  }
}

.sticker {
  background: $color-primary;
  border-radius: rem-calc($sticker-size-mobile);
  color: $white;
  display: block;
  font-family: $font-family-display;
  @include font-size(13, 11.375);
  height: rem-calc($sticker-size-mobile);
  text-align: center;
  text-decoration: none;
  @include text-uppercase;
  white-space: nowrap;
  width: rem-calc($sticker-size-mobile);
  //override text-uppercase
  line-height: rem-calc($sticker-size-mobile);

  @include media($bp-large) {
    border-radius: rem-calc($sticker-size);
    height: rem-calc($sticker-size);
    line-height: rem-calc($sticker-size);
    width: rem-calc($sticker-size);
  }
}

a.sticker {
  &:hover,
  &:focus {
    background: $color-secondary;
    color: $white;
  }
}

.flexbox .sticker {
  align-items: center;
  display: flex;
  flex-direction: column;
  justify-content: center;
  line-height: 1.2;
}

// Local Variables
$height-banner-logo-small: $height-banner-logo * 1.3333333333;
$height-banner-logo-large: $height-banner-logo * 2.4;
$width-banner-logo-small: $width-banner-logo * 1.33333333333;
$width-banner-logo-large: $width-banner-logo * 2.4;

.c-banner {
  background: $color-background;
  box-shadow: 0 2px 4px 0 $color-shadow;
  height: rem-calc($height-banner);
  position: relative;
  z-index: $z-index-header;

  @include media($bp-medium) {
    height: rem-calc($height-banner-large);
  }
}

.c-banner__logo-wrapper {
  background: $color-background;
  box-shadow: 0 0 4px 0 $color-shadow;
  display: block;
  height: rem-calc($height-banner-logo);
  @include layout-inner-left-edge;
  padding: 0 5px 8px;
  position: absolute;
  top: 0;
  width: rem-calc($width-banner-logo);

  @include media($bp-small) {
    height: rem-calc($height-banner-logo-small);
    width: rem-calc($width-banner-logo-small);
  }

  @include media($bp-medium) {
    height: rem-calc($height-banner-logo-large);
    padding: 0 16px 20px 14px;
    width: rem-calc($width-banner-logo-large);
  }
}

// Banner Logo
.c-banner__logo {
  @include image-replace('/wp-content/themes/guny/assets/img/NYCDOH_GrowingUP_logo_CMYK_Mobile_Final.png');
  background-position: center bottom;
  height: 100%;
  width: 100%;
}

.c-banner__nav {
  float: right;
  line-height: 1;
  padding-top: rem-calc(18);

  @include media($bp-medium) {
    padding-top: rem-calc(20);
  }
}

.c-banner__link {
  color: $color-primary;
  font-family: $font-family-display;
  font-size: rem-calc(12);
  font-weight: $font-weight-extrablack;
  position: relative;
  text-decoration: none;
  @include text-uppercase;

  &:hover,
  &:focus {
    color: $color-secondary;
  }

  @include media($bp-medium) {
    font-size: rem-calc($font-size-small);
    letter-spacing: 1.1px;
  }
}

// Banner Icon
//
// SVG icon. Does not appear in styleguide
.c-banner__icon {
  display: inline-block;
  height: 1.25em;
  margin-top: rem-calc(-4);
  transition: all .25s ease-in;
  vertical-align: middle;
  width: 1.25em;
}

.c-banner__icon__search {
  opacity: 1;

  .is-active & {
    opacity: 0;
  }
}

.c-banner__icon__close {
  left: 0;
  opacity: 0;
  position: absolute;
  top: rem-calc(2);

  .is-active & {
    opacity: 1;
  }
}

.c-footer {
  background: $color-background-footer;
  color: $color-text-invert;
  padding: 60px 0;
}

// Footer Section
.c-footer__section {
  @include media($bp-large) {
    @include span-columns(6);
  }
}

.c-footer__logos {
  @include row;
}

// Individual Footer Logo
.c-footer__logo {
  @include span-columns(6);

  + .c-footer__logo {
    @include omega;
  }
  @include media($bp-medium) {
    @include span-columns(4 of 12);

    + .c-footer__logo {
      @include shift(2);
    }
  }
}

// Footer Logo Image
.c-footer__logo img {
  max-height: rem-calc(77);
  max-width: 100%;

  @include media($bp-medium) {
    max-height: none;
  }
}

.c-footer__nav {
  @include row;
  line-height: 1.2;
  padding-top: 35px;

  @include media($bp-large) {
    padding-top: 40px;
  }
}

// Footer Navigation Column
.c-footer__column {
  @include span-columns(6 of 12);
  @include omega(even);
}

.c-footer__nav-item {
  list-style: none;
  margin-bottom: 8px;
}

.c-footer__nav-item--spaced {
  margin-top: 54px;
}

// Footer Link
.c-footer__link {
  color: $color-text-invert;
  font-family: $font-family-display;
  font-weight: $font-weight-extrablack;
  text-decoration: none;

  &:hover,
  &:focus {
    color: $color-secondary;
  }
}

// Footer Signup Area
.c-footer__signup {
  margin-top: 80px;

  @include media($bp-large) {
    margin-top: 0;
    @include span-columns(4);
    @include shift(2);
  }
}

.c-footer__footer {
  font-family: $font-family-display;
  font-size: rem-calc(14);
  font-weight: $font-weight-extrablack;
  line-height: 1.2;
  margin-top: 65px;
}

.c-list-box {
  display: block;
  padding: rem-calc(6 4 2);
}

.c-list-box--tertiary {
  padding: 0;
}

// List Box Item
//
// Section within the list box. Encompasses the list heading and any sublists.
.c-list-box__item {
  display: block;
  color: $color-text-invert;
  list-style: none;
  margin-bottom: rem-calc(4);
  position: relative;
}

.c-list-box--secondary .c-list-box__item {
  margin-bottom: rem-calc(2);
}

// List Box Heading
//
// Top level list item or section heading.
// If used with the accordion pattern, this will be whatever the accordion drawer
// heading is.
//
.c-list-box__heading {
  border-radius: rem-calc(3);
  display: block;
  font-family: $font-family-display;
  @include font-size-x-large;
  font-weight: $font-weight-extrablack;
  margin-bottom: 0;
  text-align: left;
  width: 100%;
}

.c-list-box--primary .c-list-box__heading,
.c-list-box--quaternary .c-list-box__heading {
  background: $color-primary;
  color: $color-text-invert;
}

.c-list-box--primary .c-list-box__heading {
  @include text-uppercase;
  padding: rem-calc(12 20);

  @include media($bp-large) {
    padding: rem-calc(28 40);
  }
}

.c-list-box--secondary .c-list-box__heading,
.c-list-box--quaternary .c-list-box__heading {
  padding: rem-calc(13 28);
}

.c-list-box--secondary .c-list-box__heading {
  background: $color-background-shade;
  border-radius: rem-calc(3);
  color: $color-primary;

  .icon {
    color: $color-text-weak;
  }

  // :focus styling is necessary for keyboard users so that it's clear what item is currently selected
  &:hover,
  &:focus {
    color: $color-secondary;
    outline: none;

    .icon {
      color: $color-text;
    }
  }
}

.c-list-box--tertiary .c-list-box__heading {
  background: $color-primary;
  color: $color-text-invert;
  @include font-size-large;
  line-height: 1.1;
  min-height: rem-calc(56);
  padding: rem-calc(10 30);
}

.c-list-box--quaternary .c-list-box__heading {
  @include media($bp-large) {
    padding: rem-calc(28);
  }
}

// List Box Subheading
//
// Optional subtitle on a list box heading
.c-list-box__subheading {
  display: block;
  @include font-size-large;
  font-weight: $font-weight-medium;
}

.c-list-box--primary .c-list-box__subheading {
  text-transform: none;
}

// List Box Content
//
// The sublist of items within a box
.c-list-box--primary .c-list-box__content {
  background: $color-primary;
  color: $color-text-invert;
}

.c-list-box--secondary .c-list-box__content {
  background: $color-primary;
}

.c-list-box--secondary .c-list-box__content,
.c-list-box--quaternary .c-list-box__content {
  font-family: $font-family-display;
  font-weight: $font-weight-extrablack;
}

.c-list-box--quaternary .c-list-box__content {
  background: $color-secondary;
  border-radius: 0 0 3px 3px;
  color: $color-text-invert;
  margin-top: -1px;
}

// List Box Subitem
//
// Item within a sublist. If the sublist is a list of links, this class may be applied
// to the a element rather than the li element.
.c-list-box__subitem {
  display: block;
  text-decoration: none;
}

.c-list-box--primary .c-list-box__subitem {
  padding: rem-calc(3 20);

  &:first-child {
    padding-top: rem-calc(30);
  }

  &:last-child {
    padding-bottom: rem-calc(30);
  }

  @include media($bp-large) {
    padding: rem-calc(3 40);

    &:last-child {
      padding-bottom: rem-calc(40);
    }

  }
}

.c-list-box--secondary .c-list-box__subitem {
  @include text-uppercase;

  &:hover,
  &:focus {
    background: $color-secondary;
  }
}

.c-list-box--secondary .c-list-box__subitem,
.c-list-box--quaternary .c-list-box__subitem {
  color: $color-text-invert;
  @include font-size-small;
  padding: rem-calc(16 28);
}

.c-list-box--tertiary .c-list-box__subitem {
  background: $color-background-shade;
  color: $color-text;
  line-height: 1.2;
  padding: rem-calc(15 30 15 42);
  text-indent: rem-calc(-27);
  margin-bottom: rem-calc(2);

  .icon {
    color: rgba($color-text, .2);
    margin-right: rem-calc(12);
    margin-top: rem-calc(2);
  }

  @include media($bp-large) {
    padding-left: rem-calc(60);
  }
}

.c-list-box--quaternary .c-list-box__subitem {
  &:hover,
  &:focus {
    background: $very-dark-blue;
  }
}

// List Box Border
.c-list-box__border {
  border-radius: 3px;
  padding: rem-calc(5 0);
}

.c-block-list--shade {
  background: $color-background-shade;
}

.c-block-list__heading {
  color: $very-dark-gray-transparent;
  @include font-size-small;
  margin-bottom: rem-calc(-20);
  @include text-uppercase;
}

// Block List Text
//
// Content that appears before the block list
.c-block-list--shade .c-block-list__text {
  color: $dark-gray;
}

// Block List Link
.c-block-list__link {
  float: right;
  text-decoration: none;
  text-transform: none;

  &:hover,
  &:focus {
    color: $color-text;
  }
}

// Block List Inner
//
// Inner container within a block list
.c-block-list__inner {
  margin-bottom: rem-calc(35);
  margin-top: rem-calc(35);

  @include media($bp-medium) {
    margin-bottom: rem-calc(45);
    margin-top: rem-calc(30);
  }
}

// Block List Item
//
// Individual item within the list
.c-block-list__item {
  padding: rem-calc(8 0);
}

.c-details-list {
  background: $color-background-shade;
  border-radius: 3px;
  padding: rem-calc(10 0);
}

// Details List Term
.c-details-list__term {
  font-family: $font-family-display;
  @include font-size-large;
  font-weight: $font-weight-extrablack;
  line-height: 1.3;
  padding: rem-calc(15 20 0);

  @include media($bp-medium) {
    padding: rem-calc(17 30 0);
  }
}

// Details List Description
.c-details-list__descr {
  padding: rem-calc(0 20 15);

  @include media($bp-medium) {
    padding: rem-calc(0 30 17);
  }
}

.c-details-list__descr + .c-details-list__term {
  border-top: 2px solid $white;
}

.form-field,
.facetwp-search {
  appearance: none;
  background: $white;
  border: 1px solid transparent;
  border-radius: rem-calc(3);
  color: $color-text;
  @include font-size-small;
  font-weight: $font-weight-semibold;
  padding: rem-calc(10 15);
}

input[type="search"].form-field {
  appearance: none;
}

// Size Variations

// Large
.form-field--large {
  @include font-size-x-large;
  line-height: normal;
  padding: rem-calc(10 40 10 20);

  @include media($bp-medium) {
    padding: rem-calc(20 60 20 25);
  }
}

.form-field--full-width {
  width: 100%;
}

// Icons

// Layout
.form-field__icon-container {
  position: relative;
}

.form-field__icon {
  display: block;
  color: $color-primary;
  cursor: pointer;
  height: rem-calc(20);
  margin-top: rem-calc(-10);
  position: absolute;
  right: rem-calc(20);
  top: 50%;
  width: rem-calc(20);

  @include media($bp-medium) {
    right: rem-calc(25);
  }

  &:hover,
  &:focus {
    color: $color-secondary;
  }

  + .form-field__icon {
    right: rem-calc(50);

    @include media($bp-medium) {
      right: rem-calc(55);
    }
  }
}

.form-field__icon--weak {
  color: $color-text-weak;

  &:hover,
  &:focus {
    color: $color-text;
  }
}

// Icon Size Variations

// Large
.form-field__icon--large {
  height: rem-calc(20);
  margin-top: rem-calc(-15);
  width: rem-calc(20);

  @include media($bp-medium) {
    height: rem-calc(30);
    margin-top: rem-calc(-15);
    width: rem-calc(30);

    + .form-field__icon--large {
      right: rem-calc(65);
    }
  }
}

.checkbox {
  cursor: pointer;
  position: relative;
}

.checkbox__field {
  position: absolute;
  opacity: 0;
  z-index: -1;
}

.checkbox__indicator {
  background: $white;
  border-radius: 3px;
  border: 2px solid $white;
  display: inline-block;
  color: transparent;
  height: rem-calc(20);
  width: rem-calc(20);
  vertical-align: middle;
}

.checkbox__field:checked + .checkbox__indicator {
  color: $color-secondary;
}

.checkbox__field:focus + .checkbox__indicator {
  border-color: $dark-gray;
}

.checkbox__field.is-error + .checkbox__indicator {
  border-color: $color-secondary;
}

.signup-form {
  @include font-size-small;
  h2 {
    margin-bottom: 0;
  }
}

// Signup Form Error
.signup-form__error {
  p {
    margin-top: rem-calc(20);
  }
}

.signup-form__input {
  display: inline-block;
  margin: rem-calc(24) 0 0;
  position: relative;
  z-index: 1;
}

.signup-form__input--long {
  width: 100%;

  @include media($bp-medium) {
    width: rem-calc(240);
  }
}

.signup-form__input--short {
  width: 100%;
  @include media($bp-medium) {
    max-width: rem-calc(153);
    min-width: rem-calc(100);
    width: calc(100% - 15.3125rem);
  }
}

.signup-form__input--full {
  width: 100%;
}

// Signup Form Field
//
// .is-error - Error state
//
// Text input field
.signup-form__field {
  border: 2px solid transparent;
  width: 100%;

  &:focus {
    border-color: $dark-gray;
    outline: none;
  }

  &.is-error {
    border-color: $color-secondary;
  }
}

// Signup Form Label
.signup-form__label {
  overflow: hidden;
  padding: 0 rem-calc(15);
  position: absolute;
  width: 100%;
}

.no-csstransforms3d .signup-form__label {
  bottom: rem-calc(10);
}

.no-csstransforms3d .is-filled .signup-form__label {
  bottom: 100%;
}

.csstransforms3d .signup-form__label,
.styleguide .signup-form__label {
  bottom: 100%;
  transform: translate3d(0, #{rem-calc(37)}, 0);
  transition: transform 250ms ease-in-out;
}

.csstransforms3d .is-filled .signup-form__label,
.styleguide .is-filled .signup-form__label {
  transform: translate3d(0, 0, 0);
}

// Signup Form Label Content
//
// Inner content within a label. The data-content
.signup-form__label-content {
  color: $color-secondary;
  display: block;
  position: relative;
  width: 100%;
}

.csstransforms3d .signup-form__label-content {
  transition: transform 250ms ease-in-out;

  &:after {
    bottom: 100%;
    color: $color-text-invert;
    content: attr(data-content);
    font-family: $font-family-display;
    @include font-size-x-small;
    font-weight: $font-weight-extrablack;
    left: 0;
    position: absolute;
    text-transform: uppercase;
    width: 100%;
  }
}

.no-csstransforms3d .is-filled .signup-form__label-content {
  color: $color-text-invert;
  font-family: $font-family-display;
  @include font-size-x-small;
  font-weight: $font-weight-extrablack;
  text-transform: uppercase;
}

.csstransforms3d .is-filled .signup-form__label-content,
.styleguide .is-filled .signup-form__label-content {
  transform: translate3d(0, 100%, 0);
}

// Signup Form Fieldset
.signup-form__fieldset {
  margin-top: rem-calc(24);
}

// Signup Form Checkbox
//
// Wrapper around checkbox input, label, and hidden field (if its a Tools checkbox)
.signup-form__checkbox {
  display: inline-block;
  line-height: rem-calc(20);
  margin-right: rem-calc(20);
  margin-top: rem-calc(10);
  vertical-align: middle;
}

// Signup Form Button
.signup-form__button {
  margin-top: rem-calc(20);
}

.c-signup-form__success {
  font-family: $font-family-display;
  @include font-size(21, 24);
  font-weight: $font-weight-extrablack;
  text-align: center;
}

.c-content-tout {
  color: $color-text-weak;
  @include font-size-small;
}

.c-content-tout__title {
  color: $color-text;
  font-family: $font-family-display;
  @include font-size-large;
  font-weight: $font-weight-extrablack;
  letter-spacing: rem-calc(.44);
  line-height: 1.2;
  margin: 0;
  text-decoration: none;

  a {
    text-decoration: inherit;
  }
}

.c-content-tout__subtitle {
  font-family: $font-family-display;
  font-weight: $font-weight-extrablack;
  margin: rem-calc(7 0 3);
  text-transform: uppercase;

  @include media($bp-small) {
    margin: rem-calc(10 0 1);
  }

  .icon--xsmall {
    position: relative;
    top: 2px;
    vertical-align: top;

    @include media($bp-small) {
      top: 3px;
    }
  }
}

.c-content-tout__link {
  @include font-size(14);
  text-decoration: underline;

  a {
    &:hover,
    &:focus {
      color: $color-text;
    }
  }
}

.c-card {
  background: $color-background;
  display: flex;
  flex-flow: column;
  margin-right: rem-calc($sticker-size-mobile / 2);
  padding: rem-calc(25 ($sticker-size-mobile / 2) + 10 25 21);
  position: relative;

  @include media($bp-large) {
    margin-right: rem-calc($sticker-size / 2);
    padding: rem-calc(30);
    padding-right: rem-calc(($sticker-size / 2) + 10);
  }
}

.c-card--hero {
  background-position: top center;
  background-size: 100% auto;
  background-repeat: no-repeat;
  padding-top: percentage(123/335);

  @include media($bp-medium) {
    padding-top: percentage(266/760);
  }
}

.c-card__sticker {
  order: 1;
  position: absolute;
  right: rem-calc(-$sticker-size-mobile / 2);
  top: rem-calc(10);

  .event-day {
    display: none;
  }

  @include media($bp-large) {
    right: rem-calc(-$sticker-size / 2);
  }
}

.flexbox .c-card__sticker {
  .event-day,
  .event-month-date {
    display: block;
  }
}

.c-card__title {
  font-family: $font-family-display;
  @include font-size-xx-large;
  font-weight: $font-weight-extrablack;
  line-height: 1.2;
  margin: rem-calc(0 0 12);
  order: 3;
  text-decoration: none;

  a {
    text-decoration: inherit;
  }
}

.c-card__tags {
  margin: rem-calc(0 0 10);
  order: 5;
}

.c-card__subtitle {
  color: $color-text-weak;
  font-family: $font-family-display;
  font-weight: $font-weight-extrablack;
  @include font-size(15);
  letter-spacing: .5px;
  margin: rem-calc(0 0 10);
  order: 4;

  .icon--xsmall {
    position: relative;
    top: rem-calc(3);
    vertical-align: top;
  }

  p + p {
    margin-top: 0;
  }
}

.c-card__subtitle strong {
  color: $color-text;
  font-weight: inherit;
}

.c-card--subtitle-first .c-card__subtitle {
  order: 2;
}

.c-card__body {
  @include font-size-small;
  margin: rem-calc(0 0 5);
  order: 6;
}

.c-card__link {
  color: $color-text-weak;
  @include font-size(14);
  order: 7;

  a {
    &:hover,
    &:focus {
      color: $color-text;
    }
  }
}

.c-card__label {
  background: $color-background;
  border-radius: 3px;
  box-shadow: 0 2px 4px 0 $color-shadow;
  color: $dark-gray;
  font-family: $font-family-display;
  @include font-size-x-small;
  font-weight: $font-weight-extrablack;
  padding: rem-calc(10);
  position: absolute;
  @include text-uppercase;
  top: rem-calc(-16);
}

.c-hero {
  background-position: top center;
  background-repeat: no-repeat;
  background-size: contain;
  color: $color-text-invert;
  overflow: hidden;
  padding: percentage(94/375) 0 rem-calc(33);
  position: relative;
  text-align: center;

  @include media($bp-medium) {
    background-size: cover;
    text-align: left;
    padding: rem-calc(92 40 80);
  }
}

.c-hero--light {
  color: $color-text;
  padding: percentage(57/375) 0 rem-calc(65);

  @include media($bp-medium) {
    padding: rem-calc(92 40 36);
  }
}

.c-hero {
  @include media($bp-xlarge) {
    padding: rem-calc(113 80 60);
  }
}

.c-hero--large {
  padding: percentage(126/375) 0 rem-calc(65);

  @include media($bp-medium) {
    padding: rem-calc(165 40 80);
  }

  @include media($bp-xlarge) {
    padding: rem-calc(227 80 80);
  }
}

.c-hero--long-text {
  text-align: left;
}

// Hero Background Variations
.c-hero--bg-primary {
  background: $color-primary;
}

.c-hero--bg-secondary {
  background-color: $color-secondary;
}

// Hero Title
//
// .c-hero__title--long - Variation for longer titles. Matches h1 styling
//
.c-hero__title {
  @include font-size-xxx-large;
  line-height: 1;
  padding-top: rem-calc(35);
  text-transform: uppercase;
  // Set z-index for text to appear on top of bg image
  z-index: 1;
}

.c-hero__title,
.c-hero__title--long {
  position: relative;
}

.flexbox .c-hero__title,
.flexbox .c-hero__title--long {
  @include media($bp-medium) {
    display: flex;
    flex-flow: row nowrap;
    align-items: center;
  }
}

// Hero Sticker
.c-hero__sticker {
  background-color: $color-secondary;
  bottom: 100%;
  box-shadow: 0 2px 4px 0 $color-shadow;
  display: inline-block;
  left: 0;
  //top: rem-calc($sticker-size-mobile / -2);
  position: absolute;

  @include media($bp-medium) {
    box-shadow: none;
    height: rem-calc(65);
    line-height: rem-calc(65);
    margin-right: rem-calc(20);
    position: static;
    width: rem-calc(65);
  }
}

// Hero Tagline
.c-hero__tagline {
  font-family: $font-family-display;
  font-size: rem-calc($font-size-xx-large * $font-size-ratio);
  font-weight: $font-weight-extrablack;
  line-height: 1.2;
  margin-top: rem-calc(10);
  position: relative;
  z-index: 1; // Set z-index for text to appear on top of bg image

  @include media($bp-medium) {
    font-size: rem-calc($font-size-xx-large);
  }

  @include media($bp-large) {
    @include span-columns(6);
    @include omega;
  }
}

.c-hero--long-text .c-hero__tagline {
  font-size: rem-calc($font-size-large * $font-size-ratio);
  line-height: 1.3;
  margin-top: 0;

  + .c-hero__tagline {
    margin-top: rem-calc(15);
  }

  @include media($bp-medium) {
    font-size: rem-calc($font-size-large);
  }

  @include media($bp-large) {
    @include span-columns(9);
    @include omega;
  }
}

// Hero Frill
//
// Decorative element (i.e. a paint splatter) added to the hero
.c-hero__frill {
  height: auto;
  position: absolute;
  top: percentage(60/375);
  right: percentage(-50/375);
  width: percentage(140/375);

  @include media($bp-medium) {
    bottom: percentage(-87/480);
    top: auto;
    right: percentage(-114/1440);
    width: percentage(404/1440);
  }

  @include media($bp-xlarge) {
    bottom: -87px;
    right: -114px;
    width: 404px;
  }
}

// Hero Full BG
//
// Full background overlay added to the hero
.c-hero__full-bg {
  height: 100%;
  left: 0;
  min-width: 100%;
  position: absolute;
  top: 0;
  width: auto;
}

// Hero Label
.c-hero__label {
  display: block;
  font-family: $font-family-display;
  font-weight: $font-weight-extrablack;
  @include font-size(21,24);
  margin-bottom: rem-calc(15);
  text-align: center;
}

.styleguide-example {
  .c-side-hero {
    height: 100%;
  }
}

.c-side-hero__image {
  background: $color-primary center center no-repeat;
  background-size: cover;
  height: 53.3333333vw;

  @include media($bp-large) {
    height: 100%;
  }

  .styleguide-example & {
    background-image: url(/wp-content/uploads/2016/07/hero-home.jpg);
  }
}

.c-side-hero__content {
  color: $white;
  padding: rem-calc(40 15);

  @include media($bp-mobile-only) {
    background: $color-primary;
    @include splatter(top, left);
    @include splatter(bottom, right);
  }

  @include media($bp-large) {
    bottom: 0;
    left: 0;
    padding: rem-calc(60);
    position: absolute;
    width: 60%;
  }
}

@include media($bp-large) {
  .js {
    .c-side-hero__content {
      position: fixed;
    }

    .is-bottom .c-side-hero__content {
      left: rem-calc(-4); // accounts for border on container
      position: absolute;
      width: 100%;
    }
  }
}

.c-side-hero__title {
  @include font-size-x-large;
  line-height: 1.2;
  position: relative;
  text-align: center;
  z-index: $z-index-hero-title;

  @include media($bp-large) {
    margin-bottom: rem-calc(20);
    text-align: left;
  }
}

.c-side-hero__title__large {
  @include font-size-xxx-large;
  text-transform: uppercase;
  white-space: nowrap;

  .rotating-text__entry {
    display: block;
    width: 100%;
  }
}

.c-arrow-pager {
  font-family: $font-family-display;
  font-weight: $font-weight-extrablack;
  @include font-size-x-large;
  padding-top: 10px;
  margin: 0 0 35px;
  text-align: center;
  @include text-uppercase;

  @include media($bp-large) {
    padding-top: 0;
  }
}

.c-arrow-pager__arrow {
  color: $very-dark-gray-transparent;
  text-decoration: none;
}

.c-tip {
  @include row;
  background: $color-primary;
  color: $color-text-invert;
  padding-top: rem-calc($sticker-size-mobile / 2);
  position: relative;
  margin-top: rem-calc(($sticker-size-mobile / 2) + $horizontal-gutter);

  @include media($bp-large) {
    padding-top: rem-calc($sticker-size / 2);
    margin-top: rem-calc(($sticker-size / 2) + $horizontal-gutter);
  }
}

// Tip Sticker
.c-tip__sticker {
  margin-left: rem-calc($sticker-size-mobile / -2);
  position: absolute;
  left: 50%;
  top: rem-calc($sticker-size-mobile / -2);

  @include media($bp-large) {
    margin-left: rem-calc($sticker-size / -2);
    top: rem-calc($sticker-size / -2);
  }
}

// Tip Inner Wrapper
// Optiona. Used if you want to restrict the width of tip content
.c-tip__inner {
  @include media($bp-medium) {
    @include span-columns(4 of 8);
    @include shift(2 of 8);
  }
}

// Tip Content
.c-tip__content {
  font-family: $font-family-display;
  @include font-size-x-large;
  font-weight: 900;
  line-height: 1.2;
}

// Tip Footer
.c-tip__footer {
  @include font-size-small;
  margin-top: 1em;

  p + p {
    margin-top: 0;
  }
}

// Tip Link
.c-tip__link {
  &:hover,
  &:focus {
    color: darken($color-text-invert, 15%);
  }
}

.c-alert {
  display: none;
}

.js .c-alert.is-open {
  background: $white;
  bottom: 0;
  display: block;
  left: 0;
  padding: 0 4px;
  position: fixed;
  text-align: center;
  width: 100%;
  z-index: $z-index-alert;

  p + p {
    margin-top: 0;
  }
}

// Inner banner content
.c-alert__banner {
  background: $very-dark-gray;
  border-radius: 3px 3px 0 0;
  color: $color-text-invert;
  font-family: $font-family-display;
  @include font-size-large;
  font-weight: $font-weight-extrablack;
  padding: 25px 0;
}

// Alert Banner Button
.c-alert__button {
  color: $color-text-invert;
  position: absolute;
  right: 10px;
  top: 0;

  &:hover,
  &:focus {
    color: darken($color-text-invert, 15%);
  }
}
