sections.css:
// Common styles across buttons
%button-base {
  border: 2px solid $color-text-invert;
  border-radius: rem-calc(25);
  display: inline-block;
  font-family: $font-family-display;
  font-size: rem-calc(18);
  font-weight: $font-weight-extrablack;
  line-height: 1;
  padding: rem-calc(14 30);
  text-align: center;
  text-decoration: none;
  transition: $button-transition;
}

.button--primary {
  @extend %button-base;
  background: $color-text-invert;
  color: $color-text;
  letter-spacing: 1px;
  text-transform: uppercase;

  &:hover,
  &:focus {
    background: transparent;
    color: $color-text-invert;
  }
}

.button--outline {
  @extend %button-base;
  background: transparent;
  letter-spacing: 1px;

  &--gray {
    border-color: $color-text;
    color: $color-text;
    opacity: .7;

    &:hover,
    &:focus {
      color: $color-text;
      opacity: 1;
    }
  }

  &--white {
    border-color: $color-text-invert;
    color: $color-text-invert;

    &:hover,
    &:focus {
      background: $color-text-invert;
      color: $color-primary;
    }
  }

}

.button--pill {
  @extend %button-base;
  background: $color-primary;
  border: 0;
  border-radius: rem-calc(15);
  color: $color-text-invert;
  font-size: rem-calc($font-size-x-small-mobile);
  margin: rem-calc(0 2 4 0);
  padding: rem-calc(10 17);
  @include text-uppercase;

  &:hover,
  &:focus {
    background: $color-secondary;
    color: $color-text-invert;
  }
}

.button--pill--alt {
  background: $color-secondary;

  &:hover,
  &:focus {
    background: $very-dark-blue;
  }
}

// Size Variations

// Full-width
.button--full-width {
  width: 100%;
}

.button--text {
  color: $color-primary;
  min-height: 0;

  &:hover,
  &:focus {
    color: $color-secondary;
    outline: none;
  }
}

// Local Variables
$height-banner-logo-small: $height-banner-logo * 1.3333333333;
$height-banner-logo-large: $height-banner-logo * 2.4;
$width-banner-logo-small: $width-banner-logo * 1.33333333333;
$width-banner-logo-large: $width-banner-logo * 2.4;

.c-banner {
  background: $color-background;
  box-shadow: 0 0 4px 0 $color-shadow;
  height: $height-banner;
  position: relative;
  z-index: $z-index-header;

  @include media($bp-large) {
    height: $height-banner-large;
  }
}

.c-banner__logo-wrapper {
  background: $color-background;
  box-shadow: 0 0 4px 0 $color-shadow;
  display: block;
  height: $height-banner-logo;
  @include layout-inner-left-edge;
  padding: 0 5px 8px;
  position: absolute;
  top: 0;
  width: $width-banner-logo;

  @include media($bp-small) {
    height: $height-banner-logo-small;
    width: $width-banner-logo-small;
  }

  @include media($bp-large) {
    height: $height-banner-logo-large;
    padding: 0 16px 20px 14px;
    width: $width-banner-logo-large;
  }
}

// Banner Logo
.c-banner__logo {
  @include image-replace('/wp-content/themes/guny/assets/img/GrowingUpNYC-logo-stacked.gif');
  background-position: center bottom;
  height: 100%;
  width: 100%;
}

.c-banner__nav {
  float: right;
  line-height: 1;
  padding-top: rem-calc(18);

  @include media($bp-large) {
    padding-top: rem-calc(20);
  }
}

.c-banner__link {
  color: $color-primary;
  font-family: $font-family-display;
  @include font-size-small;
  font-weight: $font-weight-extrablack;
  position: relative;
  text-decoration: none;
  @include text-uppercase;

  &:hover,
  &:focus {
    color: $color-secondary;
  }

  @include media($breakpoint-font) {
    letter-spacing: 1.1px;
  }
}

// Banner Icon
//
// SVG icon. Does not appear in styleguide
.c-banner__icon {
  display: inline-block;
  height: 1.25em;
  margin-top: rem-calc(-4);
  transition: all .25s ease-in;
  vertical-align: middle;
  width: 1.25em;
}

.c-banner__icon__search {
  opacity: 1;

  .is-active & {
    opacity: 0;
  }
}

.c-banner__icon__close {
  left: 0;
  opacity: 0;
  position: absolute;
  top: rem-calc(2);

  .is-active & {
    opacity: 1;
  }
}

.c-footer {
  background: $color-background-footer;
  color: $color-text-invert;
  padding: 36px 0 60px;
}

// Footer Logo
.c-footer__logo {
  @include span-columns(7);
  @include omega;
  @include media($bp-medium) {
    @include span-columns(3);
    max-width: rem-calc(250);
  }
}

// Footer Navigation
.c-footer__nav {
  @include row;
  clear: left;
  line-height: 1.2;
  padding-top: 28px;

  @include media($bp-large) {
    @include span-columns(6);
  }
}

// Footer Navigation Column
.c-footer__column {
  @include span-columns(2 of 4);
  @include omega(even);
}

// Footer Navigation Item
.c-footer__nav-item {
  margin-bottom: 8px;
}

// Footer Link
.c-footer__link {
  color: $color-text-invert;
  font-family: $font-family-display;
  font-weight: $font-weight-extrablack;
  text-decoration: none;

  &:hover,
  &:focus {
    color: $color-secondary;
  }
}

.c-list-box {
  display: block;
  padding: rem-calc(6 4 2);
}

// List Box Item
//
// Section within the list box. Encompasses the list heading and any sublists.
.c-list-box__item {
  display: block;
  list-style: none;
  margin-bottom: rem-calc(4);
}

.c-list-box--secondary .c-list-box__item {
  margin-bottom: rem-calc(2);
}

// List Box Heading
//
// Top level list item or section heading.
// If used with the accordion pattern, this will be whatever the accordion drawer
// heading is.
//
.c-list-box__heading {
  border-radius: rem-calc(3);
  display: block;
  font-family: $font-family-display;
  @include font-size(21, 24);
  font-weight: $font-weight-extrablack;
  margin-bottom: 0;
  text-align: left;
  width: 100%;
}

.c-list-box--primary .c-list-box__heading {
  background: $color-primary;
  color: $color-text-invert;
  padding: rem-calc(12 20);
  @include text-uppercase;

  @include media($bp-large) {
    padding: rem-calc(28 40);
  }
}

.c-list-box--secondary .c-list-box__heading {
  background: $color-background-shade;
  border-radius: rem-calc(3);
  color: $color-primary;
  padding: rem-calc(13 28);

  .icon {
    color: $color-text-weak;
  }

  // :focus styling is necessary for keyboard users so that it's clear what item is currently selected
  &:hover,
  &:focus {
    color: $color-secondary;
    outline: none;

    .icon {
      color: $color-text;
    }
  }
}

.c-list-box--tertiary .c-list-box__heading {
  background: $color-primary;
  color: $color-text-invert;
  @include font-size-large;
  line-height: 1.1;
  min-height: rem-calc(56);
  padding: rem-calc(10 30);
}

// List Box Subheading
//
// Optional subtitle on a list box heading
.c-list-box__subheading {
  display: block;
  font-weight: $font-weight-medium;

  @include media($bp-large) {
    font-size: .875em;
  }
}

.c-list-box--primary .c-list-box__subheading {
  text-transform: none;
}

// List Box Content
//
// The sublist of items within a box
.c-list-box--primary .c-list-box__content {
  background: $color-primary;
  color: $color-text-invert;
}

.c-list-box--secondary .c-list-box__content {
  background: $color-primary;
  font-family: $font-family-display;
  font-weight: $font-weight-extrablack;
}

// List Box Subitem
//
// Item within a sublist. If the sublist is a list of links, this class may be applied
// to the a element rather than the li element.
.c-list-box__subitem {
  display: block;
  text-decoration: none;
}

.c-list-box--primary .c-list-box__subitem {
  padding: rem-calc(3 20);

  &:last-child {
    padding-bottom: rem-calc(30);
  }

  @include media($bp-large) {
    padding: rem-calc(3 40);

    &:last-child {
      padding-bottom: rem-calc(40);
    }

  }
}

.c-list-box--secondary .c-list-box__subitem {
  color: $color-text-invert;
  font-size: rem-calc(16);
  padding: rem-calc(16 28);
  @include text-uppercase;

  &:hover,
  &:focus {
    background: $color-secondary;
  }
}

.c-list-box--tertiary .c-list-box__subitem {
  background: $color-background-shade;
  line-height: 1.2;
  padding: rem-calc(15 30 15 42);
  text-indent: rem-calc(-27);
  margin-bottom: rem-calc(2);

  .icon {
    color: rgba($color-text, .2);
    margin-right: rem-calc(12);
    margin-top: rem-calc(-2);
  }

  @include media($bp-large) {
    padding-left: rem-calc(60);
  }
}

// List Box Border
.c-list-box__border {
  border-radius: 3px;
  padding: rem-calc(5 0);
}

.c-block-list--shade {
  background: $color-background-shade;
}

.c-block-list__heading {
  color: $very-dark-gray-transparent;
  @include font-size($font-size-x-small, $font-size-small);
  margin-bottom: rem-calc(-20);
  @include text-uppercase;
}

// Block List Text
//
// Content that appears before the block list
.c-block-list__text {
  margin-top: rem-calc(30);
}

.c-block-list--shade .c-block-list__text {
  color: $dark-gray;
}

// Block List Link
.c-block-list__link {
  float: right;
  text-decoration: none;
  text-transform: none;

  &:hover,
  &:focus {
    color: $color-text;
  }
}

// Block List Inner
//
// Inner container within a block list
.c-block-list__inner {
  margin-bottom: rem-calc(35);
  margin-top: rem-calc(35);

  @include media($bp-medium) {
    margin-bottom: rem-calc(45);
    margin-top: rem-calc(30);
  }
}

// Block List Item
//
// Individual item within the list
.c-block-list__item {
  padding: rem-calc(8 0);
}

.form-field,
.facetwp-search {
  appearance: none;
  background: $white;
  border: 1px solid transparent;
  border-radius: rem-calc(3);
  font-size: rem-calc($font-size-regular);
  font-weight: $font-weight-semibold;
  margin: rem-calc(0 0 30);
  padding: rem-calc(10 50 10 15);
}

// Size Variations

// Large
.form-field--large {
  font-size: rem-calc($font-size-x-large-mobile);
  margin: rem-calc(0 0 35);
  padding: rem-calc(10 40 10 20);

  @include media($bp-medium) {
    font-size: rem-calc($font-size-x-large);
    margin: rem-calc(0 0 50);
    padding: rem-calc(20 60 20 25);
  }
}

.form-field--full-width {
  width: 100%;
}

// Icons

// Layout
.form-field__icon-container {
  position: relative;
}

.form-field__icon {
  display: inline-block;
  color: $color-primary;
  cursor: pointer;
  height: rem-calc(20);
  margin-top: rem-calc(-35);
  position: absolute;
  right: rem-calc(20);
  top: 50%;
  width: rem-calc(20);

  @include media($bp-medium) {
    right: rem-calc(25);
  }

  &:hover,
  &:focus {
    color: $color-secondary;
  }
}

// Icon Size Variations

// Large
.form-field__icon--large {
  height: rem-calc(20);
  margin-top: rem-calc(-30);
  width: rem-calc(20);

  @include media($bp-medium) {
    height: rem-calc(30);
    margin-top: rem-calc(-40);
    width: rem-calc(30);
  }
}

.c-content-tout {
  color: $color-text-weak;
  @include font-size-small;
}

.c-content-tout__title {
  color: $color-text;
  font-family: $font-family-display;
  @include font-size-large;
  font-weight: $font-weight-extrablack;
  letter-spacing: rem-calc(.44);
  line-height: 1.2;
  text-decoration: none;

  a {
    text-decoration: inherit;
  }
}

.c-content-tout__subtitle {
  font-family: $font-family-display;
  font-weight: $font-weight-extrablack;
  margin: rem-calc(7 0 3);
  text-transform: uppercase;

  @include media($bp-small) {
    margin: rem-calc(10 0 1);
  }

  .icon--xsmall {
    position: relative;
    top: 2px;
    vertical-align: top;

    @include media($bp-small) {
      top: 3px;
    }
  }
}

.c-content-tout__link {
  @include font-size($font-size-x-small-mobile, 14);
  text-decoration: underline;

  &:hover,
  &:focus {
    color: $color-text;
  }
}

// Local Variables
$sticker-size-mobile: 80;
$sticker-size: 100;

.c-card {
  background: $color-background;
  display: flex;
  flex-flow: column;
  margin-right: rem-calc($sticker-size-mobile / 2);
  padding: rem-calc(25 15);
  padding-right: rem-calc(($sticker-size-mobile / 2) + 10);
  position: relative;

  @include media($bp-medium) {
    margin-right: rem-calc($sticker-size / 2);
    padding: rem-calc(30);
    padding-right: rem-calc(($sticker-size / 2) + 10);
  }
}

.c-card--hero {
  background-position: top center;
  background-size: 100% auto;
  background-repeat: no-repeat;
  padding-top: percentage(123/335);

  @include media($bp-medium) {
    padding-top: percentage(266/760);
  }
}

.c-card__sticker {
  background: $color-primary;
  border-radius: rem-calc($sticker-size-mobile);
  color: $white;
  display: block;
  font-family: $font-family-display;
  @include font-size-x-small;
  height: rem-calc($sticker-size-mobile);
  order: 1;
  position: absolute;
  right: rem-calc(-$sticker-size-mobile / 2);
  text-align: center;
  text-decoration: none;
  top: rem-calc(10);
  @include text-uppercase;
  white-space: nowrap;
  width: rem-calc($sticker-size-mobile);
  //override text-uppercase
  line-height: rem-calc($sticker-size-mobile);

  .event-day {
    display: none;
  }

  @include media($bp-medium) {
    border-radius: rem-calc($sticker-size);
    height: rem-calc($sticker-size);
    line-height: rem-calc($sticker-size);
    right: rem-calc(-$sticker-size / 2);
    width: rem-calc($sticker-size);
  }
}

a.c-card__sticker {
  &:hover,
  &:focus {
    background: $color-secondary;
    color: $white;
  }
}

.c-card__title {
  font-family: $font-family-display;
  font-size: rem-calc(20);
  font-weight: $font-weight-extrablack;
  line-height: 1.2;
  margin: rem-calc(0 0 5);
  order: 3;
  text-decoration: none;

  @include media($bp-small) {
    @include font-size(22, $font-size-x-large);
  }

  a {
    text-decoration: inherit;
  }
}

.c-card__tags {
  margin: rem-calc(0 0 10);
  order: 5;
}

.c-card__subtitle {
  color: $color-text-weak;
  font-family: $font-family-display;
  font-weight: $font-weight-extrablack;
  font-size: rem-calc(15);
  letter-spacing: .5px;
  margin: rem-calc(0 0 10);
  order: 4;

  .icon--xsmall {
    position: relative;
    top: rem-calc(3);
    vertical-align: top;
  }
}

.c-card__subtitle strong {
  color: $color-text;
  font-weight: inherit;
}

.c-card--subtitle-first .c-card__subtitle {
  order: 2;
}

.c-card__body {
  @include font-size(14, $font-size-small);
  margin: rem-calc(0 0 5);
  order: 6;
}

.c-card__link {
  color: $color-text-weak;
  font-size: rem-calc(14);
  text-decoration: underline;
  order: 7;

  a {
    text-decoration: underline;

    &:hover,
    &:focus {
      color: $color-text;
    }
  }
}

.c-hero {
  background-position: top center;
  background-repeat: no-repeat;
  background-size: contain;
  color: $color-text-invert;
  overflow: hidden;
  padding: percentage(126/375) 0 rem-calc(65);
  position: relative;
  text-align: center;

  @include media($bp-medium) {
    background-size: cover;
    text-align: left;
    padding: rem-calc(165 40 40);
  }

  @include media($bp-xlarge) {
    padding: rem-calc(227 80 80);
  }
}

// Hero Background Variations
.c-hero--bg-primary {
  background: $color-primary;
}

// Hero Title
.c-hero__title {
  @include font-size-xx-large;
  line-height: 1;
  padding-top: rem-calc(35);
  text-transform: uppercase;
}

// Hero Tagline
.c-hero__tagline {
  font-family: $font-family-display;
  @include font-size-x-large;
  font-weight: $font-weight-extrablack;
  line-height: 1.2;

  @include media($bp-medium) {
    @include span-columns(6);
    @include omega;
  }
}

// Hero Frill
//
// Decorative element (i.e. a paint splatter) added to the hero
.c-hero__frill {
  height: auto;
  position: absolute;
  top: percentage(60/375);
  right: percentage(-50/375);
  width: percentage(140/375);

  @include media($bp-medium) {
    bottom: percentage(-87/480);
    top: auto;
    right: percentage(-114/1440);
    width: percentage(404/1440);
    .stop1 {
      stop-opacity: .3;
    }
  }

  @include media($bp-xlarge) {
    bottom: -87px;
    right: -114px;
    width: 404px;
  }
}

// Hero Full BG
//
// Full background overlay added to the hero
.c-hero__full-bg {
  height: 100%;
  left: 0;
  min-width: 100%;
  position: absolute;
  top: 0;
  width: auto;
}

.styleguide-example {
  .c-side-hero {
    height: 100%;
  }
}

.c-side-hero__image {
  background: $color-primary center center no-repeat;
  background-size: cover;
}

.c-side-hero__image--desktop {
  display: none;

  @include media($bp-medium) {
    display: block;
    height: 100%;
  }

  .styleguide-example & {
    background-image: url(/wp-content/uploads/2016/07/hero-home.jpg);
  }
}

.c-side-hero__image--mobile {
  height: rem-calc(200);

  @include media($bp-medium) {
    display: none;
  }

  .styleguide-example & {
    background-image: url(/wp-content/uploads/2016/07/hero-mobile-700x0-c-default.jpg);
  }
}

.c-side-hero__content {
  color: $white;
  padding: rem-calc(40 15);

  @include media($bp-mobile-only) {
    background: $color-primary;
    @include splatter(top, left);
    @include splatter(bottom, right);
  }

  @include media($bp-medium) {
    bottom: 0;
    left: 0;
    padding: rem-calc(60);
    position: absolute;
    width: 60%;
  }
}

@include media($bp-medium) {
  .js {
    .c-side-hero__content {
      position: fixed;
    }

    .is-bottom .c-side-hero__content {
      left: rem-calc(-4); // accounts for border on container
      position: absolute;
      width: 100%;
    }
  }
}

.c-side-hero__title {
  @include font-size(18, 24);
  line-height: 1.2;
  position: relative;
  text-align: center;
  z-index: $z-index-hero-title;

  @include media($bp-medium) {
    margin-bottom: rem-calc(20);
    text-align: left;
  }
}

.c-side-hero__title__large {
  @include font-size(40, 66);
  text-transform: uppercase;
  white-space: nowrap;

  .rotating-text__entry {
    display: block;
    width: 100%;
  }
}

section.css:
// Common styles across buttons
%button-base {
  border: 2px solid $color-text-invert;
  border-radius: rem-calc(25);
  display: inline-block;
  font-family: $font-family-display;
  font-size: rem-calc(18);
  font-weight: $font-weight-extrablack;
  line-height: 1;
  padding: rem-calc(14 30);
  text-align: center;
  text-decoration: none;
  transition: $button-transition;
}

.button--primary {
  @extend %button-base;
  background: $color-text-invert;
  color: $color-text;
  letter-spacing: 1px;
  text-transform: uppercase;

  &:hover,
  &:focus {
    background: transparent;
    color: $color-text-invert;
  }
}

.button--outline {
  @extend %button-base;
  background: transparent;
  letter-spacing: 1px;

  &--gray {
    border-color: $color-text;
    color: $color-text;
    opacity: .7;

    &:hover,
    &:focus {
      color: $color-text;
      opacity: 1;
    }
  }

  &--white {
    border-color: $color-text-invert;
    color: $color-text-invert;

    &:hover,
    &:focus {
      background: $color-text-invert;
      color: $color-primary;
    }
  }

}

.button--pill {
  @extend %button-base;
  background: $color-primary;
  border: 0;
  border-radius: rem-calc(15);
  color: $color-text-invert;
  font-size: rem-calc($font-size-x-small-mobile);
  margin: rem-calc(0 2 4 0);
  padding: rem-calc(10 17);
  @include text-uppercase;

  &:hover,
  &:focus {
    background: $color-secondary;
    color: $color-text-invert;
  }
}

.button--pill--alt {
  background: $color-secondary;

  &:hover,
  &:focus {
    background: $very-dark-blue;
  }
}

// Size Variations

// Full-width
.button--full-width {
  width: 100%;
}

.button--text {
  color: $color-primary;
  min-height: 0;

  &:hover,
  &:focus {
    color: $color-secondary;
    outline: none;
  }
}

// Local Variables
$height-banner-logo-small: $height-banner-logo * 1.3333333333;
$height-banner-logo-large: $height-banner-logo * 2.4;
$width-banner-logo-small: $width-banner-logo * 1.33333333333;
$width-banner-logo-large: $width-banner-logo * 2.4;

.c-banner {
  background: $color-background;
  box-shadow: 0 0 4px 0 $color-shadow;
  height: $height-banner;
  position: relative;
  z-index: $z-index-header;

  @include media($bp-large) {
    height: $height-banner-large;
  }
}

.c-banner__logo-wrapper {
  background: $color-background;
  box-shadow: 0 0 4px 0 $color-shadow;
  display: block;
  height: $height-banner-logo;
  @include layout-inner-left-edge;
  padding: 0 5px 8px;
  position: absolute;
  top: 0;
  width: $width-banner-logo;

  @include media($bp-small) {
    height: $height-banner-logo-small;
    width: $width-banner-logo-small;
  }

  @include media($bp-large) {
    height: $height-banner-logo-large;
    padding: 0 16px 20px 14px;
    width: $width-banner-logo-large;
  }
}

// Banner Logo
.c-banner__logo {
  @include image-replace('/wp-content/themes/guny/assets/img/GrowingUpNYC-logo-stacked.gif');
  background-position: center bottom;
  height: 100%;
  width: 100%;
}

.c-banner__nav {
  float: right;
  line-height: 1;
  padding-top: rem-calc(18);

  @include media($bp-large) {
    padding-top: rem-calc(20);
  }
}

.c-banner__link {
  color: $color-primary;
  font-family: $font-family-display;
  @include font-size-small;
  font-weight: $font-weight-extrablack;
  position: relative;
  text-decoration: none;
  @include text-uppercase;

  &:hover,
  &:focus {
    color: $color-secondary;
  }

  @include media($breakpoint-font) {
    letter-spacing: 1.1px;
  }
}

// Banner Icon
//
// SVG icon. Does not appear in styleguide
.c-banner__icon {
  display: inline-block;
  height: 1.25em;
  margin-top: rem-calc(-4);
  transition: all .25s ease-in;
  vertical-align: middle;
  width: 1.25em;
}

.c-banner__icon__search {
  opacity: 1;

  .is-active & {
    opacity: 0;
  }
}

.c-banner__icon__close {
  left: 0;
  opacity: 0;
  position: absolute;
  top: rem-calc(2);

  .is-active & {
    opacity: 1;
  }
}

.c-footer {
  background: $color-background-footer;
  color: $color-text-invert;
  padding: 36px 0 60px;
}

// Footer Logo
.c-footer__logo {
  @include span-columns(7);
  @include omega;
  @include media($bp-medium) {
    @include span-columns(3);
    max-width: rem-calc(250);
  }
}

// Footer Navigation
.c-footer__nav {
  @include row;
  clear: left;
  line-height: 1.2;
  padding-top: 28px;

  @include media($bp-large) {
    @include span-columns(6);
  }
}

// Footer Navigation Column
.c-footer__column {
  @include span-columns(2 of 4);
  @include omega(even);
}

// Footer Navigation Item
.c-footer__nav-item {
  margin-bottom: 8px;
}

// Footer Link
.c-footer__link {
  color: $color-text-invert;
  font-family: $font-family-display;
  font-weight: $font-weight-extrablack;
  text-decoration: none;

  &:hover,
  &:focus {
    color: $color-secondary;
  }
}

.c-list-box {
  display: block;
  padding: rem-calc(6 4 2);
}

// List Box Item
//
// Section within the list box. Encompasses the list heading and any sublists.
.c-list-box__item {
  display: block;
  list-style: none;
  margin-bottom: rem-calc(4);
}

.c-list-box--secondary .c-list-box__item {
  margin-bottom: rem-calc(2);
}

// List Box Heading
//
// Top level list item or section heading.
// If used with the accordion pattern, this will be whatever the accordion drawer
// heading is.
//
.c-list-box__heading {
  border-radius: rem-calc(3);
  display: block;
  font-family: $font-family-display;
  @include font-size(21, 24);
  font-weight: $font-weight-extrablack;
  margin-bottom: 0;
  text-align: left;
  width: 100%;
}

.c-list-box--primary .c-list-box__heading {
  background: $color-primary;
  color: $color-text-invert;
  padding: rem-calc(12 20);
  @include text-uppercase;

  @include media($bp-large) {
    padding: rem-calc(28 40);
  }
}

.c-list-box--secondary .c-list-box__heading {
  background: $color-background-shade;
  border-radius: rem-calc(3);
  color: $color-primary;
  padding: rem-calc(13 28);

  .icon {
    color: $color-text-weak;
  }

  // :focus styling is necessary for keyboard users so that it's clear what item is currently selected
  &:hover,
  &:focus {
    color: $color-secondary;
    outline: none;

    .icon {
      color: $color-text;
    }
  }
}

.c-list-box--tertiary .c-list-box__heading {
  background: $color-primary;
  color: $color-text-invert;
  @include font-size-large;
  line-height: 1.1;
  min-height: rem-calc(56);
  padding: rem-calc(10 30);
}

// List Box Subheading
//
// Optional subtitle on a list box heading
.c-list-box__subheading {
  display: block;
  font-weight: $font-weight-medium;

  @include media($bp-large) {
    font-size: .875em;
  }
}

.c-list-box--primary .c-list-box__subheading {
  text-transform: none;
}

// List Box Content
//
// The sublist of items within a box
.c-list-box--primary .c-list-box__content {
  background: $color-primary;
  color: $color-text-invert;
}

.c-list-box--secondary .c-list-box__content {
  background: $color-primary;
  font-family: $font-family-display;
  font-weight: $font-weight-extrablack;
}

// List Box Subitem
//
// Item within a sublist. If the sublist is a list of links, this class may be applied
// to the a element rather than the li element.
.c-list-box__subitem {
  display: block;
  text-decoration: none;
}

.c-list-box--primary .c-list-box__subitem {
  padding: rem-calc(3 20);

  &:last-child {
    padding-bottom: rem-calc(30);
  }

  @include media($bp-large) {
    padding: rem-calc(3 40);

    &:last-child {
      padding-bottom: rem-calc(40);
    }

  }
}

.c-list-box--secondary .c-list-box__subitem {
  color: $color-text-invert;
  font-size: rem-calc(16);
  padding: rem-calc(16 28);
  @include text-uppercase;

  &:hover,
  &:focus {
    background: $color-secondary;
  }
}

.c-list-box--tertiary .c-list-box__subitem {
  background: $color-background-shade;
  line-height: 1.2;
  padding: rem-calc(15 30 15 42);
  text-indent: rem-calc(-27);
  margin-bottom: rem-calc(2);

  .icon {
    color: rgba($color-text, .2);
    margin-right: rem-calc(12);
    margin-top: rem-calc(-2);
  }

  @include media($bp-large) {
    padding-left: rem-calc(60);
  }
}

// List Box Border
.c-list-box__border {
  border-radius: 3px;
  padding: rem-calc(5 0);
}

.c-block-list--shade {
  background: $color-background-shade;
}

.c-block-list__heading {
  color: $very-dark-gray-transparent;
  @include font-size($font-size-x-small, $font-size-small);
  margin-bottom: rem-calc(-20);
  @include text-uppercase;
}

// Block List Text
//
// Content that appears before the block list
.c-block-list__text {
  margin-top: rem-calc(30);
}

.c-block-list--shade .c-block-list__text {
  color: $dark-gray;
}

// Block List Link
.c-block-list__link {
  float: right;
  text-decoration: none;
  text-transform: none;

  &:hover,
  &:focus {
    color: $color-text;
  }
}

// Block List Inner
//
// Inner container within a block list
.c-block-list__inner {
  margin-bottom: rem-calc(35);
  margin-top: rem-calc(35);

  @include media($bp-medium) {
    margin-bottom: rem-calc(45);
    margin-top: rem-calc(30);
  }
}

// Block List Item
//
// Individual item within the list
.c-block-list__item {
  padding: rem-calc(8 0);
}

.form-field,
.facetwp-search {
  appearance: none;
  background: $white;
  border: 1px solid transparent;
  border-radius: rem-calc(3);
  font-size: rem-calc($font-size-regular);
  font-weight: $font-weight-semibold;
  margin: rem-calc(0 0 30);
  padding: rem-calc(10 50 10 15);
}

// Size Variations

// Large
.form-field--large {
  font-size: rem-calc($font-size-x-large-mobile);
  margin: rem-calc(0 0 35);
  padding: rem-calc(10 40 10 20);

  @include media($bp-medium) {
    font-size: rem-calc($font-size-x-large);
    margin: rem-calc(0 0 50);
    padding: rem-calc(20 60 20 25);
  }
}

.form-field--full-width {
  width: 100%;
}

// Icons

// Layout
.form-field__icon-container {
  position: relative;
}

.form-field__icon {
  display: inline-block;
  color: $color-primary;
  cursor: pointer;
  height: rem-calc(20);
  margin-top: rem-calc(-35);
  position: absolute;
  right: rem-calc(20);
  top: 50%;
  width: rem-calc(20);

  @include media($bp-medium) {
    right: rem-calc(25);
  }

  &:hover,
  &:focus {
    color: $color-secondary;
  }
}

// Icon Size Variations

// Large
.form-field__icon--large {
  height: rem-calc(20);
  margin-top: rem-calc(-30);
  width: rem-calc(20);

  @include media($bp-medium) {
    height: rem-calc(30);
    margin-top: rem-calc(-40);
    width: rem-calc(30);
  }
}

.c-content-tout {
  color: $color-text-weak;
  @include font-size-small;
}

.c-content-tout__title {
  color: $color-text;
  font-family: $font-family-display;
  @include font-size-large;
  font-weight: $font-weight-extrablack;
  letter-spacing: rem-calc(.44);
  line-height: 1.2;
  text-decoration: none;

  a {
    text-decoration: inherit;
  }
}

.c-content-tout__subtitle {
  font-family: $font-family-display;
  font-weight: $font-weight-extrablack;
  margin: rem-calc(7 0 3);
  text-transform: uppercase;

  @include media($bp-small) {
    margin: rem-calc(10 0 1);
  }

  .icon--xsmall {
    position: relative;
    top: 2px;
    vertical-align: top;

    @include media($bp-small) {
      top: 3px;
    }
  }
}

.c-content-tout__link {
  @include font-size($font-size-x-small-mobile, 14);
  text-decoration: underline;

  &:hover,
  &:focus {
    color: $color-text;
  }
}

// Local Variables
$sticker-size-mobile: 80;
$sticker-size: 100;

.c-card {
  background: $color-background;
  display: flex;
  flex-flow: column;
  margin-right: rem-calc($sticker-size-mobile / 2);
  padding: rem-calc(25 15);
  padding-right: rem-calc(($sticker-size-mobile / 2) + 10);
  position: relative;

  @include media($bp-medium) {
    margin-right: rem-calc($sticker-size / 2);
    padding: rem-calc(30);
    padding-right: rem-calc(($sticker-size / 2) + 10);
  }
}

.c-card--hero {
  background-position: top center;
  background-size: 100% auto;
  background-repeat: no-repeat;
  padding-top: percentage(123/335);

  @include media($bp-medium) {
    padding-top: percentage(266/760);
  }
}

.c-card__sticker {
  background: $color-primary;
  border-radius: rem-calc($sticker-size-mobile);
  color: $white;
  display: block;
  font-family: $font-family-display;
  @include font-size-x-small;
  height: rem-calc($sticker-size-mobile);
  order: 1;
  position: absolute;
  right: rem-calc(-$sticker-size-mobile / 2);
  text-align: center;
  text-decoration: none;
  top: rem-calc(10);
  @include text-uppercase;
  white-space: nowrap;
  width: rem-calc($sticker-size-mobile);
  //override text-uppercase
  line-height: rem-calc($sticker-size-mobile);

  .event-day {
    display: none;
  }

  @include media($bp-medium) {
    border-radius: rem-calc($sticker-size);
    height: rem-calc($sticker-size);
    line-height: rem-calc($sticker-size);
    right: rem-calc(-$sticker-size / 2);
    width: rem-calc($sticker-size);
  }
}

a.c-card__sticker {
  &:hover,
  &:focus {
    background: $color-secondary;
    color: $white;
  }
}

.c-card__title {
  font-family: $font-family-display;
  font-size: rem-calc(20);
  font-weight: $font-weight-extrablack;
  line-height: 1.2;
  margin: rem-calc(0 0 5);
  order: 3;
  text-decoration: none;

  @include media($bp-small) {
    @include font-size(22, $font-size-x-large);
  }

  a {
    text-decoration: inherit;
  }
}

.c-card__tags {
  margin: rem-calc(0 0 10);
  order: 5;
}

.c-card__subtitle {
  color: $color-text-weak;
  font-family: $font-family-display;
  font-weight: $font-weight-extrablack;
  font-size: rem-calc(15);
  letter-spacing: .5px;
  margin: rem-calc(0 0 10);
  order: 4;

  .icon--xsmall {
    position: relative;
    top: rem-calc(3);
    vertical-align: top;
  }
}

.c-card__subtitle strong {
  color: $color-text;
  font-weight: inherit;
}

.c-card--subtitle-first .c-card__subtitle {
  order: 2;
}

.c-card__body {
  @include font-size(14, $font-size-small);
  margin: rem-calc(0 0 5);
  order: 6;
}

.c-card__link {
  color: $color-text-weak;
  font-size: rem-calc(14);
  text-decoration: underline;
  order: 7;

  a {
    text-decoration: underline;

    &:hover,
    &:focus {
      color: $color-text;
    }
  }
}

.c-hero {
  background-position: top center;
  background-repeat: no-repeat;
  background-size: contain;
  color: $color-text-invert;
  overflow: hidden;
  padding: percentage(126/375) 0 rem-calc(65);
  position: relative;
  text-align: center;

  @include media($bp-medium) {
    background-size: cover;
    text-align: left;
    padding: rem-calc(165 40 40);
  }

  @include media($bp-xlarge) {
    padding: rem-calc(227 80 80);
  }
}

// Hero Background Variations
.c-hero--bg-primary {
  background: $color-primary;
}

// Hero Title
.c-hero__title {
  @include font-size-xx-large;
  line-height: 1;
  padding-top: rem-calc(35);
  text-transform: uppercase;
}

// Hero Tagline
.c-hero__tagline {
  font-family: $font-family-display;
  @include font-size-x-large;
  font-weight: $font-weight-extrablack;
  line-height: 1.2;

  @include media($bp-medium) {
    @include span-columns(6);
    @include omega;
  }
}

// Hero Frill
//
// Decorative element (i.e. a paint splatter) added to the hero
.c-hero__frill {
  height: auto;
  position: absolute;
  top: percentage(60/375);
  right: percentage(-50/375);
  width: percentage(140/375);

  @include media($bp-medium) {
    bottom: percentage(-87/480);
    top: auto;
    right: percentage(-114/1440);
    width: percentage(404/1440);
    .stop1 {
      stop-opacity: .3;
    }
  }

  @include media($bp-xlarge) {
    bottom: -87px;
    right: -114px;
    width: 404px;
  }
}

// Hero Full BG
//
// Full background overlay added to the hero
.c-hero__full-bg {
  height: 100%;
  left: 0;
  min-width: 100%;
  position: absolute;
  top: 0;
  width: auto;
}

.styleguide-example {
  .c-side-hero {
    height: 100%;
  }
}

.c-side-hero__image {
  background: $color-primary center center no-repeat;
  background-size: cover;
}

.c-side-hero__image--desktop {
  display: none;

  @include media($bp-medium) {
    display: block;
    height: 100%;
  }

  .styleguide-example & {
    background-image: url(/wp-content/uploads/2016/07/hero-home.jpg);
  }
}

.c-side-hero__image--mobile {
  height: rem-calc(200);

  @include media($bp-medium) {
    display: none;
  }

  .styleguide-example & {
    background-image: url(/wp-content/uploads/2016/07/hero-mobile-700x0-c-default.jpg);
  }
}

.c-side-hero__content {
  color: $white;
  padding: rem-calc(40 15);

  @include media($bp-mobile-only) {
    background: $color-primary;
    @include splatter(top, left);
    @include splatter(bottom, right);
  }

  @include media($bp-medium) {
    bottom: 0;
    left: 0;
    padding: rem-calc(60);
    position: absolute;
    width: 60%;
  }
}

@include media($bp-medium) {
  .js {
    .c-side-hero__content {
      position: fixed;
    }

    .is-bottom .c-side-hero__content {
      left: rem-calc(-4); // accounts for border on container
      position: absolute;
      width: 100%;
    }
  }
}

.c-side-hero__title {
  @include font-size(18, 24);
  line-height: 1.2;
  position: relative;
  text-align: center;
  z-index: $z-index-hero-title;

  @include media($bp-medium) {
    margin-bottom: rem-calc(20);
    text-align: left;
  }
}

.c-side-hero__title__large {
  @include font-size(40, 66);
  text-transform: uppercase;
  white-space: nowrap;

  .rotating-text__entry {
    display: block;
    width: 100%;
  }
}
