sections.css:
.c-banner {
  background: $color-background;
  box-shadow: 0 0 4px 0 $color-shadow;
  height: $height-banner;
  margin-bottom: $height-banner-logo - $height-banner;

  @include media($bp-small) {
    margin-bottom: ($height-banner-logo-small) - $height-banner;
  }

  @include media($bp-large) {
    height: $height-banner-large;
    margin-bottom: ($height-banner-logo-large) - $height-banner-large;
  }
}

.c-banner__logo-wrapper {
  background: $color-background;
  box-shadow: 0 0 4px 0 $color-shadow;
  display: block;
  float: left;
  height: $height-banner-logo;
  padding: 0 5px 8px;
  width: $width-banner-logo;

  @include media($bp-small) {
    height: $height-banner-logo-small;
    width: $width-banner-logo-small;
  }

  @include media($bp-large) {
    height: $height-banner-logo-large;
    padding: 0 16px 20px 14px;
    width: $width-banner-logo-large;
  }
}

// Banner Logo
.c-banner__logo {
  background: url(/wp-content/themes/guny/assets/img/GrowingUpNYC-logo-stacked.gif) center bottom no-repeat;
  background-size: contain;
  display: block;
  height: 100%;
  overflow: hidden;
  text-indent: 100%;
  white-space: nowrap;
  width: 100%;
}

.c-banner__nav {
  float: right;
  line-height: 1;
  padding-top: rem-calc(18);

  @include media($bp-large) {
    padding-top: rem-calc(20);
  }
}

.c-banner__link {
  font-family: $font-family-display;
  @include font-size-small;
  font-weight: $font-weight-extrablack;
  @include text-uppercase;

  @include media($breakpoint-font) {
    letter-spacing: 1.1px;
  }
}

// Banner Icon
//
// SVG icon. Does not appear in styleguide
.c-banner__icon {
  display: inline-block;
  height: 1.25em;
  margin-top: -4px;
  vertical-align: middle;
  width: 1.25em;
}

